    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        Productos prod=new Productos();
        FlujoArchivoObjProductos flujo=new FlujoArchivoObjProductos();
        String ruta=txtRuta.getText();
        String nombreArch=txtNombreArch.getText();
        //--------------------------------------------------------------
        String codProd=txtCodigoProd.getText();
        
        txtCodigoProd.setText(flujo.BusquedaFileObj(ruta, nombreArch, codProd, prod).getCod_producto());
        txtNombreProd.setText(flujo.BusquedaFileObj(ruta, nombreArch, codProd, prod).getNombreProd());
        txtPrecio.setText(Double.toString(flujo.BusquedaFileObj(ruta, nombreArch, codProd, prod).getPR_venta()));
        txtAPantalla.append(flujo.pantalla());
        indice=flujo.contadorBusqueda();
        txtAPantalla.append("\nEl Registro est√° en la posici√≥n :"+Integer.toString(flujo.contadorBusqueda())+"\n");
        JOptionPane.showMessageDialog(null,"Proceda a Modificar el Producto");
        txtCodigoProd.requestFocus();
    }//GEN-LAST:event_btnBuscarActionPerformed

    private void btnLeerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLeerActionPerformed
        Productos prod=new Productos();
        FlujoArchivoObjProductos flujo=new FlujoArchivoObjProductos();
        String ruta=txtRuta.getText();
        String nombreArch=txtNombreArch.getText();
        //--------------------------------------------------------------
        flujo.lecturaFileObj(ruta, nombreArch, prod);
        txtAPantalla.append(flujo.pantalla());
        txtCodigoProd.requestFocus();
    }//GEN-LAST:event_btnLeerActionPerformed

    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        Productos prod=new Productos();
        FlujoArchivoObjProductos flujo=new FlujoArchivoObjProductos();
        String ruta=txtRuta.getText();
        String nombreArch=txtNombreArch.getText();
        //-------------------------------------------------------------------
        String codProd=txtCodigoProd.getText();
        String nombreProd=txtNombreProd.getText();
        double precio=Double.parseDouble(txtPrecio.getText());
        prod.setCod_producto(codProd);
        prod.setNombreProd(nombreProd);
        prod.setPR_venta(precio);
        
        flujo.escrituraFileObj(ruta, nombreArch, prod);
        JOptionPane.showMessageDialog(null,"Producto Guardado con Exito");
        //-------------------------------------------------------------------
        txtCodigoProd.setText("");
        txtNombreProd.setText("");
        txtPrecio.setText("");
        txtCodigoProd.requestFocus();
    }//GEN-LAST:event_btnGuardarActionPerformed

    private void btnModificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnModificarActionPerformed
        Productos prod=new Productos();
        Productos prodN=new Productos();
        int cont2=0;
        FlujoArchivoObjProductos flujo=new FlujoArchivoObjProductos();
        String ruta=txtRuta.getText();
        String nombreArch=txtNombreArch.getText();
        //-------------------------------------------------------------------
        String codProd=txtCodigoProd.getText();
        String nombreProd=txtNombreProd.getText();
        double precio=Double.parseDouble(txtPrecio.getText());
        prod.setCod_producto(codProd);
        prod.setNombreProd(nombreProd);
        prod.setPR_venta(precio);
        cont2=indice;
        flujo.modificarRegistro(ruta, nombreArch, prod,prodN, cont2);
        
        JOptionPane.showMessageDialog(null,"Producto Modificado con Exito");
        
    }//GEN-LAST:event_btnModificarActionPerformed
     

